import React, { useState } from 'react';
import './style.css';

function DateDifference() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [filteredData, setFilteredData] = useState([]);

  const jsonData = [
    {
      day: 0,
      temp: 68,
      image: '',
      message: 'windy',
      date: '2023-09-01',
    },
    {
      day: 1,
      temp: 74,
      image: '',
      message: 'sunny',
      date: '2023-09-02',
    },
    {
      day: 2,
      temp: 83,
      image: '',
      message: 'ThunderStorm',
      date: '2023-09-03',
    },
    {
      day: 3,
      temp: 75,
      image: '',
      message: 'Mostly Cloudy',
      date: '2023-09-04',
    },
    {
      day: 4,
      temp: 82,
      image: '',
      message: 'partly cloudy',
      date: '2023-09-05',
    },
    {
      day: 5,
      temp: 81,
      image: '',
      message: 'Rain',
      date: '2023-09-06',
    },
    {
      day: 6,
      temp: 90,
      image: '',
      message: 'sunny',
      date: '2023-09-07',
    },
  ];

  const handleSubmit = () => {
    const startDateObj = new Date(startDate);
    const endDateObj = new Date(endDate);

    // Calculate the date 7 days ago from the start date
    const sevenDaysAgo = new Date(startDateObj);
    sevenDaysAgo.setDate(startDateObj.getDate() - 7);

    // Check if the date range is exactly 7 days
    if (endDateObj - sevenDaysAgo === 7 * 24 * 60 * 60 * 1000) {
      // Fetch forecast data for the 7-day period
      const filtered = jsonData.filter((item) => {
        const itemDate = new Date(item.date);
        return itemDate >= sevenDaysAgo && itemDate <= endDateObj;
      });

      setFilteredData(filtered);
    } else {
      alert('Please select a date range with a 7-day difference.');
    }
  };

  return (
    <div className="date-difference">
      <h1>Date Range Filter</h1>
      <div className="input-container">
        <label>Start Date:</label>
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />
        <label>End Date:</label>
        <input
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />
        <button onClick={handleSubmit}>Submit</button>
      </div>
      <div className="data-container">
        {filteredData.map((item, index) => (
          <div key={index} className="data-card">
            <p>Date: {item.date}</p>
            <p>Temperature: {item.temp}</p>
            <p>Message: {item.message}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DateDifference;
