import React, { useState } from 'react';
import './style.css';
import '../Assests/Rainy.png'
import '../Assests/partlycloudy.jpeg'
import '../Assests/sunny.png'
import '../Assests/png-transparent-thunderstorm-cloud-free-content-dangerous-weather-s-angle-cloud-thunder.png'
import '../Assests/download.jpeg'
function DateDifference() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [filteredData, setFilteredData] = useState([]);

  const jsonData = [
    {
      day: 0,
      temp: 68,
      image: RainyImage,
      message: 'windy',
    },
    {
      day: 1,
      temp: 74,
      image: SunnyImage,
      message: 'sunny',
    },
    {
      day: 2,
      temp: 83,
      image: PartlyCloudyImage,
      message: 'ThunderStorm',
    },
    {
      day: 3,
      temp: 75,
      image: RainyImage,
      message: 'Mostly Cloudy',
    },
    {
      day: 4,
      temp: 82,
      image: PartlyCloudyImage,
      message: 'partly cloudy',
    },
    {
      day: 5,
      temp: 81,
      image: PartlyCloudyImage,
      message: 'Rain',
    },
    {
      day: 6,
      temp: 90,
      image: SunnyImage,
      message: 'sunny',
    },
  ];


  const handleSubmit = () => {
    setFilteredData(jsonData); 
  };

  return (
    <div className="date-difference">
      <h1>Date Range Filter</h1>
      <div className="input-container">
        <label>Start Date:</label>
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />
        <label>End Date:</label>
        <input
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />
        <button onClick={handleSubmit}>Submit</button>
      </div>
      <div className="data-container">
        {filteredData.map((item, index) => (
          <div key={index} className="data-card">

            <p>Temperature: {item.temp}</p>
            <img src={item.image} alt={item.message} />
            <p>Message: {item.message}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DateDifference;

