import React, { useState } from "react";
import './data.css';

export const Weather = () => {
    const [fromdate, setFromdate] = useState('');
    const [todate, setTodate] = useState('');
    const [data, setData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);

    const handleFromdateChange = (e) => {
        setFromdate(e.target.value);
    };

    const handleTodateChange = (e) => {
        setTodate(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const fromDateObj = new Date(fromdate);
        const toDateObj = new Date(todate);

        // Assuming data is fetched from an API, but you can replace it with your data source.
        // This is just a placeholder for your data.
        const jsonData = [
            {
                day: 0,
                temp: 68,
                image: "https://img.freepik.com/premium-psd/3d-icon-weather-forecast-illustration-cloudy-thunder-windy_771369-536.jpg?w=2000",
                message: "WINDY",
            },
            // ... Add more data here ...
        ];

        // Filter the data based on the date range
        const filteredData = jsonData.filter(item => {
            const itemDate = new Date(); // You need to replace this with your item's date property.
            return itemDate >= fromDateObj && itemDate <= toDateObj;
        });

        setData(jsonData);
        setFilteredData(filteredData);
    };

    return (
        <>
            <div>
                <input
                    type="text"
                    id="fromdate"
                    name="fromdate"
                    placeholder="From date"
                    value={fromdate}
                    onChange={handleFromdateChange}
                />
                <br /> <br />
                <input
                    type="text"
                    id="todate"
                    name="todate"
                    placeholder="To date"
                    value={todate}
                    onChange={handleTodateChange}
                />
                <br /> <br />
                <button onClick={handleSubmit}>Submit</button>
                <div className="card-container">
                    {filteredData.map((item, index) => (
                        <div key={index}>
                            <img src={item.image} alt="not found" />
                            <h4>Temp: {item.temp}</h4>
                            <h4>Message: {item.message}</h4>
                            <h4>Day: {item.day}</h4>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}

export default Weather;
