import React, { useState } from "react";
import './data.css'

const Movies=()=>{
    const [fromDate, setFromDate] = useState(""); 
    const [toDate, setToDate] = useState("");  
 const [filteredData,setFilteredData] = useState([]);

    const data=[
        {date:"02/06/2023",
         name:"indaian jones and the dial of destinity",
        image:"",
        message:"2023 2h 34m",
        rating:'6.9'},

    {date:"04/07/2023",
    name:"No Hard feelings",
     image:"https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.nohardfeelingsmovie.com%2Fgallery%2F&psig=AOvVaw0VwYz0VgzOXsV0UJtklsje&ust=1694067741173000&source=images&cd=vfe&opi=89978449&ved=0CBAQjRxqFwoTCLCSy6GslYEDFQAAAAAdAAAAABAE",
    message:"2023 2h 43m",
    rating:'6.8'},


    {date:"04/04/2023",
    name:"Astroid City",
     image:"https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.nohardfeelingsmovie.com%2Fgallery%2F&psig=AOvVaw0VwYz0VgzOXsV0UJtklsje&ust=1694067741173000&source=images&cd=vfe&opi=89978449&ved=0CBAQjRxqFwoTCLCSy6GslYEDFQAAAAAdAAAAABAE",
    message:"2023 1h 45m",
    rating:'7.1'},

    {date:"04/06/2023",
    name:"The Flash",
     image:"https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.nohardfeelingsmovie.com%2Fgallery%2F&psig=AOvVaw0VwYz0VgzOXsV0UJtklsje&ust=1694067741173000&source=images&cd=vfe&opi=89978449&ved=0CBAQjRxqFwoTCLCSy6GslYEDFQAAAAAdAAAAABAE",
    message:"2023 2h 24m",
    rating:'7.2'},
    ]


    const handleFromDateChange = (event) => {
        setFromDate(event.target.value);
    };

    const handleToDateChange = (event) => {
        setToDate(event.target.value);
    };

    const handleSubmit = () => {
        const fromDateObj = new Date(fromDate);
        const toDateObj = new Date(toDate);

        const filteredMovies = data.filter(item => {
            const movieDate = new Date(item.date);
            return movieDate >= fromDateObj && movieDate <= toDateObj;
        });

        setFilteredData(filteredMovies); // Update the state with the filtered data
    };

    return(
        <div>
            <label>From</label>
            <input type="date" id="from" name="from"   onChange={handleFromDateChange}/>
            <label>To</label>
            <input type="date" id="to" name="to" onChange={handleToDateChange}/>
            <br/><br/>
            <button onClick={handleSubmit}>submit</button>
            <div className="card-container">
              {
                filteredData.map((item)=>{
                    return(
                        <div>
                            <img src={item.image} alt="not found"/>
                            <h4>{"*"+item.rating}  *Rate</h4>
                            <h4>{item.name}</h4>
                            <h4>{item.message}</h4>
                            <h4>{item.date}</h4>
                        </div>
                    )
                })
              }
              </div>

       <h1>hello</h1>
        </div>
    )
}
export default Movies;
