import React, { useState } from 'react';
import './style.css';

function DateDifference() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [filteredData, setFilteredData] = useState([]);

  const jsonData = [
    {
      day: 0,
      temp: 68,
      image: '',
      message: 'windy',
      date: '2023-09-01',
    },
    {
      day: 1,
      temp: 74,
      image: '',
      message: 'sunny',
      date: '2023-09-02',
    },
    {
      day: 2,
      temp: 83,
      image: '',
      message: 'ThunderStorm',
    },
    {
      day: 3,
      temp: 75,
      image: '',
      message: 'Mostly Cloudy',
    },
    {
      day: 4,
      temp: 82,
      image: '',
      message: 'partly cloudy',
    },
    {
      day: 5,
      temp: 81,
      image: '',
      message: 'Rain',
    },
    {
      day: 6,
      temp: 90,
      image: '',
      message: 'sunny',
    },
  ];

  const handleSubmit = () => {
    setFilteredData(jsonData); // Set filteredData to the entire jsonData array
  };

  return (
    <div className="date-difference">
      <h1>Date Range Filter</h1>
      <div className="input-container">
        <label>Start Date:</label>
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />
        <label>End Date:</label>
        <input
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />
        <button onClick={handleSubmit}>Submit</button>
      </div>
      <div className="data-container">
        {filteredData.map((item, index) => (
          <div key={index} className="data-card">
            <p>Date: {item.date}</p>
            <p>Temperature: {item.temp}</p>
            <p>Message: {item.message}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DateDifference;
