import React,{useState,useEffect} from "react";
import './data.css';
const Date=()=> {
    const[fromDate,setFromDate]=useState('');
    const[toDate,setToDate]=useState('');
    const data=[
        {
            day:0,
            temp:68,
            image:"https://thenounproject.com/browse/icons/term/windy-weather/",
            message:"WINDY"
        },
        {
            day:1,
            temp:74,
            image:"https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.pngitem.com%2Fmiddle%2FJxxJR_simple-weather-icons-sunny-sunny-weather-icon-png%2F&psig=AOvVaw0GfwiHUk-0HFmJLLvrBkWQ&ust=1694152019485000&source=images&cd=vfe&opi=89978449&ved=0CA4QjRxqFwoTCMj-jp3ml4EDFQAAAAAdAAAAABAE",
            message:"SUNNY"  
        },
        {
            day:2,
            temp:83,
            image:"https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.pixelsquid.com%2Fpng%2Fweather-forecast-thunderstorm-2155264596754044392&psig=AOvVaw0sISPv5Y7ueNpkKymW-AgE&ust=1694152087364000&source=images&cd=vfe&opi=89978449&ved=0CA4QjRxqFwoTCKjh1L3ml4EDFQAAAAAdAAAAABAE",
            message:"THUNDERSTORMS"
        },
       {
        day:3,
            temp:75,
            image:"https://www.google.com/url?sa=i&url=https%3A%2F%2Fpngtree.com%2Fso%2Fpartly-cloudy&psig=AOvVaw0jfTFYxCVxKoa-Dz75zXLz&ust=1694152114875000&source=images&cd=vfe&opi=89978449&ved=0CA4QjRxqFwoTCLil1czml4EDFQAAAAAdAAAAABAE",
            message:"MOSTLY CLOUDY"
       },
       {
        day:4,
            temp:82,
            image:"https://www.google.com/url?sa=i&url=https%3A%2F%2Fpngtree.com%2Fso%2Fpartly-cloudy&psig=AOvVaw0jfTFYxCVxKoa-Dz75zXLz&ust=1694152114875000&source=images&cd=vfe&opi=89978449&ved=0CA4QjRxqFwoTCLil1czml4EDFQAAAAAdAAAAABAE",
            message:"MOSTLY CLOUDY"
       },
       {
        day:5,
            temp:81,
            image:"https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.transparentpng.com%2Fcats%2Fweather-report-102.html&psig=AOvVaw0BaxvAkQLfjp6FvuX5_upG&ust=1694152148729000&source=images&cd=vfe&opi=89978449&ved=0CA4QjRxqFwoTCKDaltzml4EDFQAAAAAdAAAAABAE",
            message:"RAIN"
       },
       {
        day:6,
            temp:90,
            image:"https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.pngitem.com%2Fmiddle%2FJxxJR_simple-weather-icons-sunny-sunny-weather-icon-png%2F&psig=AOvVaw0GfwiHUk-0HFmJLLvrBkWQ&ust=1694152019485000&source=images&cd=vfe&opi=89978449&ved=0CA4QjRxqFwoTCKDcmOnml4EDFQAAAAAdAAAAABAE",
            message:"SUNNY"
       }
    ]
    const[filteredData,setFilteredData]=useState(data);



const handlechange=(e)=> {
    const{date,value}=e.target;
        if(date===fromDate) {
            setFromDate(date);

        } 
        else if(date===toDate) {
            setToDate(date);
        }

}


const handleSubmit=(e)=> {
    const fromDate=new Date(fromDate);
    const toDate=new Date(toDate);
    const diffindays=Math.ceil((toDate-fromDate)/1000*60*60*24);
} 



return (


    <>
    <div><input type="text"
        id="fromDate"
        name="fromDate"
        placeholder="From date"
        onChange={handlechange} />
        <br/> <br/>
        <input type="text"
            id="toDate"
            name="toDate"
            placeholder="To date"
            onChange={handlechange} />
            <br/> <br/>
            <button onClick={handleSubmit}>Submit</button>
            <div className="card-comtainer">
            {
                filteredData.map((item)=>{
                    return (
                        <div>
                            <img src={item.image} alt="not found image"/>
                            </div>
                    )
                })
            }
            </div>
            </div>
    </>


)
        };
export default Date;
