import React, { useState } from 'react';
import './style.css';

function DateDifference() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [filteredData, setFilteredData] = useState([]);

  const jsonData = [
    {
      day: 0,
      temp: 68,
      image: '../Images/windy.jpeg',
      message: 'windy',
    },
    {
      day: 1,
      temp: 74,
      image: '../Images/sunny.png', 
      message: 'sunny',
    },
    {
      day: 2,
      temp: 83,
      image: '../Images/ThunderStorm.png', 
      message: 'ThunderStorm',
    },
    {
      day: 3,
      temp: 75,
      image: '../Images/Mostlycloudy.png',
      message: 'Mostly Cloudy',
    },
    {
      day: 4,
      temp: 82,
      image: '../Images/Partlycloudy.jpeg', 
      message: 'partly cloudy',
    },
    {
      day: 5,
      temp: 81,
      image: '../Images/Rainy.png', 
      message: 'Rain',
    },
    {
      day: 6,
      temp: 90,
      image: '../Images/sunny.png', 
      message: 'sunny',
    },
  ];



//   const handleSubmit = () => {
//     setFilteredData(jsonData); 
//   };

const handleSubmit = () => {
    const start = new Date(startDate);
    const end = new Date(endDate);
    const diffInDays = (end - start) / (1000 * 60 * 60 * 24); 

    if (diffInDays === 6) {

      setFilteredData(jsonData);
    } else {

      alert("Please select a date range of exactly 7 days.");
      setFilteredData([]); 
    }
  };


  return (
    <div className="date-difference">
      <h1>Date Range Filter</h1>
      <div className="input-container">
        <label>Start Date:</label>
        <input
          type="date"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />
        <label>End Date:</label>
        <input
          type="date"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />
        <button onClick={handleSubmit}>Submit</button>
      </div>
      <div className="data-container">
        {filteredData.map((item, index) => (
          <div key={index} className="data-card">

            <p>Temperature: {item.temp}</p>
            <img src={item.image} alt={item.message} />
            <p>Message: {item.message}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DateDifference;

