import React,{useState,useEffect} from "react";
import './data.css';
const Weather=()=> {
    const[fromdate,setfromdate]=useState('');
    const[todate,settodate]=useState('');
    const[data,setData]=useState([]);
    const[isLoading,setIsLoading]=useState(false);
    const[error,setError]=useState(null);



    const handleFromdateChange = (e) => {
        setfromdate(e.target.value);
    };

    const handleTodateChange = (e) => {
        settodate(e.target.value);
    };


const handleSubmit=(e)=> {
    e.preventDefault();
    const fromdateObj=new Date(fromdate);
    const todateObj=new Date(todate);
    const diffindays=Math.ceil((todateObj-fromdateObj)/(1000*60*60*24));


    if(diffindays!==7) {
        alert("The difference between from date and to date mustbe exactly 7");
        return;
    }
    setIsLoading(true);
    const jsondata=[
        {
            day:0,
            temp:68,
            image:"https://img.freepik.com/premium-psd/3d-icon-weather-forecast-illustration-cloudy-thunder-windy_771369-536.jpg?w=2000",
            message:"WINDY"
        },
        {
            day:1,
            temp:74,
            image:"https://w7.pngwing.com/pngs/267/400/png-transparent-free-content-blog-sunny-weather-cloud-hand-website-thumbnail.png",
            message:"SUNNY"  
        },
        {
            day:2,
            temp:83,
            image:"https://upload.wikimedia.org/wikipedia/commons/thumb/e/e7/Weather-rain-thunderstorm.svg/1200px-Weather-rain-thunderstorm.svg.png",
            message:"THUNDERSTORMS"
        },
       {
        day:3,
            temp:75,
            image:"https://upload.wikimedia.org/wikipedia/commons/thumb/f/f6/Weather-overcast.svg/1024px-Weather-overcast.svg.png",
            message:"MOSTLY CLOUDY"
       },
       {
        day:4,
            temp:82,
            image:"https://upload.wikimedia.org/wikipedia/commons/thumb/f/f6/Weather-overcast.svg/1024px-Weather-overcast.svg.png",
            message:"CLOUDY"
       },
       {
        day:5,
            temp:81,
            image:"https://img.freepik.com/premium-psd/sunny-rainy-day-weather-forecast-icon-meteorological-sign-3d-render_471402-496.jpg",
            message:"RAIN"
       },
       {
        day:6,
            temp:90,
            image:"https://w7.pngwing.com/pngs/267/400/png-transparent-free-content-blog-sunny-weather-cloud-hand-website-thumbnail.png",
            message:"SUNNY"
       }
    ]
setData(jsondata);
    // const filteredData=filteredData.map(item=>{
    //     const fromdate=new Date(item.date);
    //     const todate=new Date(item.date);
    //     <img src={item.image} alt="not found"/>
    //     return fromdate >= fromdateObj && todate <= todateObj;
    // })
    // setFilteredData(filteredData);



return (


    <>
    <div><input type="text"
        id="fromdate"
        name="fromdate"
        placeholder="From date"


        onChange={handleFromdateChange} />
        <br/> <br/>
        <input type="text"
            id="todate"
            name="todate"
            placeholder="To date"

            onChange={handleTodateChange} />
            <br/> <br/>
            <button onClick={handleSubmit}>Submit</button>
            <div className="card-container">
              {
                data.map((item)=>{
                    return(
                        <div>
                            <img src={item.image} alt="not found"/>
                            <h4>{item.temp}</h4>
                            <h4>{item.name}</h4>
                            <h4>{item.message}</h4>
                            <h4>{item.date}</h4>
                        </div>
                    )
                })
            }
              </div>




            </div>
    </>


)
        };
    }
export default Weather;
